mapper.select:
select ifnull((select us.user_name from sys_user us where us.user_id = ol.user_id),'非登录用户请求') as userName, ol.url,ol.base_url as baseUrl,ol.request_ip_address as requestIpAddress,ol.request_time as requestTime from sys_operation_log ol where 1=1
and ol.user_id in(select u.user_id from sys_user u where u.user_name like CONCAT('%',#{userName},'%'))
and ol.request_ip_address like CONCAT('%',#{requestIpAddress},'%') order by ol.request_time desc limit ${(currentPage-1)*pageSize},${pageSize}
